import Navigation from '../components/Navigation.vue';
import AstronauteCss from '../components/AstronauteCss.vue';
import * as dat from 'dat.gui';


 
export default {
    

    name: 'Home',

    components: { 
        AstronauteCss, 
        Navigation
    },

    data() {
        return {
        camera: null,
        scene: null,
        renderer: null,
        labelRenderer: null,
        satelite1: null,
        satelite2: null,
        satelite3: null,
        satelite4: null,
        rover: null,
        mars: null,
        clock: new THREE.Clock(),
        loadingManager: new THREE.LoadingManager(),
        textureLoader: new THREE.TextureLoader(this.loadingManager),
        gui : null
        }
    },

    methods: {

        init: function() {

            /**
             * Base / camera / scene / light
             */
            const container = document.getElementById('home');
            const MARS_RADIUS = 2;

            this.camera = new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, .1, 200 );
            this.camera.position.set( 10, 10, 1 );

            this.scene = new THREE.Scene();
            this.scene.background = new THREE.TextureLoader().load("./images/sky.jpg");

            const dirLight = new THREE.DirectionalLight( 0xffffff );
            const light = new THREE.PointLight(0xffffff, 1, 1000);
            dirLight.position.set( 5, 5, 5 );
            this.scene.add( dirLight, light );



            /**
             * Help and debug
           
            const axesHelper = new THREE.AxesHelper( 5 );
            this.scene.add( axesHelper );  */
            const gui = new dat.GUI()


            //MARS
            const marsGeometry = new THREE.SphereGeometry( MARS_RADIUS, 60, 60 );
            const marsMaterial = new THREE.MeshStandardMaterial( {
                map: this.textureLoader.load( './images/mars_color.jpg' ),
                displacementMap: this.textureLoader.load( './images/mars_height.jpg' ),
                displacementScale: .3,
                normalMap: this.textureLoader.load( './images/marsnormal2k.jpeg' ),
            } );
            this.mars = new THREE.Mesh( marsGeometry, marsMaterial );
            this.scene.add( this.mars );

            //debug Materials Textures
            gui.add(marsMaterial, 'displacementScale').min(0).max(5).step(.0001)



            //SATELLITE
            const sateliteGeometry = new THREE.SphereGeometry( 0, 0, 0 );   
            const roverGeometry = new THREE.SphereGeometry( 0, 2, 0 ); 
            const sateliteMaterial = new THREE.MeshPhongMaterial( {
                shininess: 0
            } );

            // satellite 1
            this.satelite1 = new THREE.Mesh( sateliteGeometry, sateliteMaterial );
            this.satelite1.scale.multiplyScalar(.1);
            this.scene.add( this.satelite1 );

            // satellite 2
            this.satelite2 = new THREE.Mesh( sateliteGeometry, sateliteMaterial );
            this.satelite2.scale.multiplyScalar(.1);
            this.scene.add( this.satelite2 );

            // satellite 3
            this.satelite3 = new THREE.Mesh( sateliteGeometry, sateliteMaterial );
            this.satelite3.scale.multiplyScalar(.1);
            this.scene.add( this.satelite3 );

            // satellite 4
            this.satelite4 = new THREE.Mesh( sateliteGeometry, sateliteMaterial );
            this.satelite4.scale.multiplyScalar(.1);
            this.scene.add( this.satelite4 );

            // rover 1
            this.rover = new THREE.Mesh( roverGeometry, sateliteMaterial );
            this.rover.scale.multiplyScalar(.009);
            this.mars.add( this.rover );
    
            
            //Satellite Object
            let fbxLoader  = new FBXLoader();
            let gltfLoader = new GLTFLoader();

            // satellite 1
            fbxLoader.load( './3DObjects/maya2sketchfab.fbx', (object) => {
                object.scale.multiplyScalar(1);
                this.satelite1.add( object );
            })
             

            // satellite 2
            gltfLoader.setPath( './3DObjects/sat2/' ).load( 'sat2.gltf', (object) => {
                object.scene.scale.multiplyScalar(.1);
                this.satelite2.add( object.scene );
            }) 

            // satellite 3
            gltfLoader.setPath( './3DObjects/sat3/' ).load( 'scene.gltf', ( object ) => {
                object.scene.scale.multiplyScalar(.9);
                this.satelite3.add( object.scene );
            }) 

            // satellite 4
            gltfLoader.setPath( './3DObjects/satelite/' ).load( 'scene.gltf', ( object ) => {
                object.scene.scale.multiplyScalar(.9);
                
                this.satelite4.add( object.scene );
            })

            // rover 1
            gltfLoader.setPath( './3DObjects/mars_rover/' ).load( 'scene.gltf', ( object ) => {
                object.scene.scale.multiplyScalar(.05);
                this.rover.add( object.scene );
                this.rover.position.set(-.5, 2.5, -.5);
            })

            // ----------------------------------------------- A href
            // satellite 1
    /*         const link1 = document.createElement('a');
            link1.href = "/curiosity";
            link1.textContent = 'Curiosity';


            // satellite 2
            const link2 = document.createElement('a');
            link2.href = '/Photo/all/opportunity';
            link2.textContent = 'Opportunity';

            // satellite 3
            /* const link3 = document.createElement('a');
            link3.href = '/spirit';
            link3.textContent = 'Spirit'; 

            
            
            
            // satellite 4
            const link5 = document.createElement('a');
            link5.href = '/Event/index';
            link5.textContent = 'Astrological events';

            // rover 1
            const link4 = document.createElement('a');
            link4.href = "/Daily/getDailyPic";
            link4.textContent = 'Daily Picture';

            // -----------------------------------------------DIV
            // satellite 1
            const satellite1Div = document.createElement('div');
            satellite1Div.className = 'label';
            satellite1Div.appendChild(link1);
            const sat1Label = new CSS2DObject( satellite1Div );
            sat1Label.position.set( 0, SATELLITE_RADIUS, 0 );
            this.satelite1.add( sat1Label );

            // satellite 2
            const satellite2Div = document.createElement( 'div' );
            satellite2Div.className = 'label';
            satellite2Div.appendChild(link2);
            const sat2Label = new CSS2DObject( satellite2Div );
            sat2Label.position.set( 0, SATELLITE_RADIUS, 0 );
            this.satelite2.add( sat2Label );

            // satellite 3
            const satellite3Div = document.createElement( 'div' );
            satellite3Div.className = 'label2';
            satellite3Div.appendChild(link3);
            const sat3Label = new CSS2DObject( satellite3Div );
            sat3Label.position.set( 0, SATELLITE_RADIUS, 0 );
            this.satelite3.add( sat3Label );

            // satellite 4
            const satellite4Div = document.createElement( 'div' );
            satellite4Div.className = 'label';
            satellite4Div.appendChild(link5);
            const sat4Label = new CSS2DObject( satellite4Div );
            sat4Label.position.set( 0, SATELLITE_RADIUS, 0 );
            this.satelite4.add( sat4Label );

            //rover 1
            const rover1Div = document.createElement( 'div' );
            rover1Div.className = 'label rover';
            rover1Div.appendChild(link4);
            const rov1Label = new CSS2DObject( rover1Div );
            this.rover.add( rov1Label );
 
            // ------------------------------------------------------ RENDERER
*/
            this.renderer = new THREE.WebGLRenderer();
            this.renderer.setPixelRatio( window.devicePixelRatio );
            this.renderer.setSize( window.innerWidth, window.innerHeight);
            container.appendChild( this.renderer.domElement );

            this.labelRenderer = new CSS2DRenderer();
            this.labelRenderer.setSize( window.innerWidth ,window.innerHeight);
            this.labelRenderer.domElement.classList.add('sky');
            this.labelRenderer.domElement.style.position = 'absolute';
            this.labelRenderer.domElement.style.top = '0px';
            container.appendChild( this.labelRenderer.domElement );


            const controls = new OrbitControls( this.camera, this.labelRenderer.domElement );
            controls.minDistance = 5;
            controls.maxDistance = 25;

            window.addEventListener( 'resize', this.onWindowResize );
        },

        onWindowResize: function() {
            this.camera.aspect = window.innerWidth / window.innerHeight;
            this.camera.updateProjectionMatrix();
            this.renderer.setSize( window.innerWidth ,window.innerHeight );
            this.labelRenderer.setSize( window.innerWidth, window.innerHeight );
        },

        animate: function(){
            requestAnimationFrame( this.animate );

            const elapsed = this.clock.getElapsedTime();
            
            this.mars.rotation.y +=  0.003;

            this.satelite1.position.set( Math.sin( elapsed/4 ) * 7, 0, Math.cos( elapsed/4 ) * 7 );
            this.satelite2.position.set( Math.sin( elapsed/-5 ) * 7, 5, Math.cos( elapsed/-5 ) * 7 );
            this.satelite3.position.set( 2, Math.sin( elapsed/-8 ) * 10,  Math.cos( elapsed/-8 ) * 10 );
            this.satelite4.position.set(  Math.sin( elapsed/5 ) * 6, 7, Math.cos( elapsed/5 ) * 6 );

            this.renderer.render( this.scene, this.camera );
            this.labelRenderer.render( this.scene, this.camera );
        }
    },

    mounted() {
        this.init();
        this.animate();
    },

}
</script>


